package com.bk;

/***
 * Leetcode - https://leetcode.com/problems/repeated-substring-pattern/
 * 
Given a non-empty string check if it can be constructed by taking a substring of it 
and appending multiple copies of the substring together. You may assume the given string
 consists of lowercase English letters only and its length will not exceed 10000.



Example 1:

Input: "abab"
Output: True
Explanation: It's the substring "ab" twice.
Example 2:

Input: "aba"
Output: False
Example 3:

Input: "abcabcabcabc"
Output: True
Explanation: It's the substring "abc" four times. (And the substring "abcabc" twice.)

 * @author bhaveshkumar.chaudhari@gmail.com
 *
 */

public class RepeatedSubstringPattern {

	public boolean repeatedSubstringPattern(String s) {
		if(s == null)
			return false;
		else if(s.isEmpty())
			return true;
		String subString = null;
		int n = s.length();
		for(int i = 1; i <= n/2; i++) {
			if(n % i == 0 && s.substring(0, i).equals(s.substring(i, i + i))) {
				subString = s.substring(0, i);				
			}
		}

		return isRepeated(s, subString, n);
	}

	public boolean isRepeated(String s, String subString, int length) {
		if(subString == null || length % subString.length() != 0) {
			return false;
		}

		String result = s.replaceAll(subString, "");
		return result.isEmpty();
	}

	public static void main(String[] args) {
		String s1 = "abab";
		String s2 = "aba";
		String s3 = "abcabcabcabc";
		String s4 = null;
		String s5 = "";
		String s6 = "abcaaabcaaabcaa";
		String s7 = "abaababaab";
		String s8 = "";
		String s9 = "ababababab";

		RepeatedSubstringPattern pattern = new RepeatedSubstringPattern();
		System.out.println("Expected true found --> " + pattern.repeatedSubstringPattern(s1));
		System.out.println("Expected false found --> " + pattern.repeatedSubstringPattern(s2));
		System.out.println("Expected true found --> " + pattern.repeatedSubstringPattern(s3));
		System.out.println("Expected false found --> " + pattern.repeatedSubstringPattern(s4));
		System.out.println("Expected true found --> " + pattern.repeatedSubstringPattern(s5));
		System.out.println("Expected true found --> " + pattern.repeatedSubstringPattern(s6));
		System.out.println("Expected true found --> " + pattern.repeatedSubstringPattern(s7));
		System.out.println("Expected false found --> " + pattern.repeatedSubstringPattern(s8));
		System.out.println("Expected true found --> " + pattern.repeatedSubstringPattern(s9));
	}
}
